import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { startWith, map } from 'rxjs/operators';
import { IFlight } from 'src/app/models/flight.interface';
import { DataService } from '../../../data.service';

@Component({
  selector: 'app-autocomplete',
  templateUrl: './autocomplete.component.html',
  styleUrls: ['./autocomplete.component.scss'],
})
export class AutocompleteComponent implements OnInit {
  public data: IFlight[] = [];

  searchTerm = new FormControl();
  // registrations: string[] = [];

  registrations: string[] = ['ciao', 'come', 'stai'];
  cities: string[] = ['ciao', 'come', 'stai'];

  filteredRegistrations!: Observable<string[]>;

  constructor(private service: DataService) {}

  ngOnInit(): void {
    // this.getData();
    this.filteredRegistrations = this.searchTerm.valueChanges.pipe(
      startWith(''),
      map((value) => this.filterSearch(value))
    );
  }

  private filterSearch(value: string): string[] {
    const filterValue = value.toLowerCase();
    return this.cities.filter((city) =>
      city.toLocaleLowerCase().includes(filterValue)
    );
  }

