}
<form>
  <mat-form-field appearance="fill">
    <input
      type="text"
      placeholder="Pick One"
      aria-label="Select a registration"
      matInput
      [formControl]="searchTerm"
      [autocomplete]="auto"
    />
    <mat-autocomplete autoActiveFirstOption #auto="matAutocomplete">
      <mat-option
        *ngFor="let city of filteredRegistrations | async"
        [value]="city"
        >{{ city }}
      </mat-option>
    </mat-autocomplete>
  </mat-form-field>
</form>
